import React, { useState } from 'react';

const EditableField = ({ value, onSave }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [inputValue, setInputValue] = useState(value);

    const handleEditClick = () => {
        setIsEditing(true);
    };

    const handleInputChange = (e) => {
        setInputValue(e.target.value);
    };

    const handleSaveClick = () => {
        onSave(inputValue);
        setIsEditing(false);
    };

    const handleCancelClick = () => {
        setInputValue(value);
        setIsEditing(false);
    };

    return (
        <div className="flex items-center">
            {isEditing ? (
                <div className="flex items-center">
                    <input
                        type="text"
                        value={inputValue}
                        onChange={handleInputChange}
                        className="border border-gray-300 rounded p-2"
                    />
                    <button onClick={handleSaveClick} className="ml-2 bg-blue-500 text-white rounded p-2">Save</button>
                    <button onClick={handleCancelClick} className="ml-2 bg-red-500 text-white rounded p-2">Cancel</button>
                </div>
            ) : (
                <div className="flex items-center">
                    <span className="mr-2">{value}</span>
                    <button onClick={handleEditClick} className="bg-yellow-500 text-white rounded p-2">Edit</button>
                </div>
            )}
        </div>
    );
};

export default EditableField;
